mapper.select:
select post.*, count(if(re_state=1, re_state, null)) po_up, count(if(re_state=-1, re_state, null)) po_down from post LEFT JOIN recommend ON po_num = re_po_num where po_co_num = #{cri.co_num}
and (po_title like concat('%', #{cri.search}, '%') or po_me_id = #{cri.search})
and po_title like concat('%', #{cri.search}, '%')
and po_me_id = #{cri.search}
group by po_num order by po_num desc limit #{cri.pageStart}, #{cri.perPageNum}

select count(*) from post where po_co_num = #{cri.co_num}

and (po_title like concat('%', #{cri.search}, '%') or po_me_id = #{cri.search})


and po_title like concat('%', #{cri.search}, '%')


and po_me_id = #{cri.search}

mapper.update:
update post
set 
	po_title = #{post.po_title},

	po_content = #{post.po_content}
where
	po_num = #{post.po_num}
